  }

  ellipse(width / 2, height / 2, size);
}

// 心臓の鼓動のようなアニメーション
//共通の定数や変数として、初めに定数や変数を宣言
const cycle = 100; // 1周期のフレーム数を設定
let count = 0; // 現在のフレーム数を設定
let size = 50; //現在の円の大きさを設定　

function setup(){ //初めに1回だけ行う処理を定義
  createCanvas(200, 200);
